import React, { ChangeEventHandler } from 'react';
import { useState } from 'react';
import './App.css';
import Item from './Components/Item';
import BackDrop from './Components/BackDrop';
import Modal from './Components/Modal';

function App() {
  const [modalIsOpen, setModalIsOpen] = useState(false);

    function previewHandle()
    {
        setModalIsOpen(true);
    }
    function ModalHandle()
    {
        setModalIsOpen(false);
    }

  return (
    <div>
      <h1>To Doo List</h1>
      <Item text="Header"/>
      <Item text="Body"/>
      <Item text="Signature"/>
      <br></br>
      <button onClick={previewHandle}>Preview</button>
      {modalIsOpen && <Modal onCancel={ModalHandle} onConfirm={ModalHandle}/>}
      {modalIsOpen && <BackDrop onClick={ModalHandle} />}
    </div>
  );
}

export default App;
