// import { useState } from 'react';
// import Modal from './Modal';
// import BackDrop from './BackDrop';
import React from 'react';


function Item(props:any)
{
    var state = {value : ''}

    function handleChange(str:string)
    {
        state = {value : str};
    }

    function htmlView()
    {
        return {state};
        //{ __html: this.md.render(this.state.value) }
    }

    return(
        <div className="card">
            <h1>{props.text}</h1>
            <div className="actions">
                <textarea className="text"
                onChange={e => handleChange(e.target.value)}></textarea>
            </div>
            {/* {modalIsOpen && <Modal onCancel={ModalHandle} onConfirm={ModalHandle}/>}
            {modalIsOpen && <BackDrop onClick={ModalHandle} />} */}
        <div
          className="content"
          dangerouslySetInnerHTML={htmlView}
        />
        </div>
    );
}

export default Item;
