import { Component, useState } from 'react';
import Modal from './Modal';
import BackDrop from './BackDrop';
import { render } from 'react-dom';
import React from 'react';
import { chdir } from 'process';
import { Statement } from 'typescript';
import ReactDOM from 'react-dom';

function Item(props:any)
{
    const [modalIsOpen, setModalIsOpen] = useState(false);

    function clickHandle()
    {
        setModalIsOpen(true);

    }
    function ModalHandle()
    {
        setModalIsOpen(false);
    }

    var state = {value : ''};

    function handleChange(targetV : string)
    {
        state = {value : targetV}
    }

    const ch = () =>
    {
        console.log(props.text)
        let x = document.getElementsByClassName('try').item(0) !;
        x.innerHTML = x.innerHTML + "<li>"+ state.value+"</li>";
        console.log(x.innerHTML);
    }

    return(
        <div className="card">
            <h1>{props.text}</h1>
            <div className='try'></div>
            <div className="actions">
                <textarea onChange={e  =>handleChange(e.target.value)}></textarea><br></br>
                <button onClick={ch}>Add</button>
            </div>
            {modalIsOpen && <Modal onCancel={ModalHandle} onConfirm={ModalHandle}/>}
            {modalIsOpen && <BackDrop onClick={ModalHandle} />}
        </div>
    );
}

export default Item;
