import { getValue } from "@testing-library/user-event/dist/utils";


function Item(props:any)
{
    var state = { value: '' };

    function handleChange(e)
    {
        state = { value: e.target.value };
    }

    return(
        <div className="card">
            <h1>{props.text}</h1>
            <div className="actions">
                <textarea className="text" value = {state.value}
                onChange={handleChange(getValue)}></textarea>
            </div>
            {/* {modalIsOpen && <Modal onCancel={ModalHandle} onConfirm={ModalHandle}/>}
            {modalIsOpen && <BackDrop onClick={ModalHandle} />} */}
        </div>
    );
}

export default Item;
