//import React from "react";
import * as React from 'react'
import Remarkable from 'remarkable-react';

class Item extends React.Component<any,any>{

  constructor(props:any)
  {
    super(props);
    this.md = new Remarkable();
    this.handleChange = this.handleChange.bind(this);
    this.add=this.add.bind(this);
    this.cle = this.cle.bind(this);
    this.state = { value: '' };
    console.log(this.state);
  }

  handleChange(e:any) {
    this.setState({value : e});
  }

  add (){
    let x = document.getElementsByClassName("try").item(0)!;
    this.setState({value : x.innerHTML + "<li>" + this.state.value + "</li><br/><hr/>"});
    //console.log(x.innerHTML);
  };

  cle()
  {
    let x = document.getElementsByClassName("try").item(0)!;
    x.innerHTML = "";
  }

  tryreturn()
  {
    return {__html: this.render(this.state.value)};
  }

  render(): React.ReactNode {
    return (
      <div className="card">
        <h2>{this.props.text}</h2>
        <div className="actions">
          <input type="text" placeholder="Enter List Items" onChange={e => this.handleChange(e.target.value)}/>
          <br></br>
          <br></br>
          <button onClick={e => this.add()}>Add</button>
          <button onClick={this.cle}>Clear</button>
          <br></br>
          <br></br>
          <hr></hr>
          <div className="try" dangerouslySetInnerHTML={this.tryreturn()}></div>
        </div>
      </div>
    );
  }

}

export default Item;
