import React, { Component } from "react";

class Item extends React.Component{

  constructor(props:any)
  {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.state = { value: '' };
  }

  handleChange(e:any) {
    //state = { value: targetV };
    this.setState({value : e});
  }

  add (){
    let x = document.getElementsByClassName("try").item(0)!;
    var  newhtml = x.innerHTML + "<li>" + this.state + "</li><br/><hr/>";
    console.log(x.innerHTML);
    return newhtml
  };

  cle()
  {
    let x = document.getElementsByClassName("try").item(0)!;
    x.innerHTML = "";
  }

  render(): React.ReactNode {
    return (
      <div className="card">
        <h2></h2>
        <div className="actions">
          <input type="text" placeholder="Enter List Items" onChange={e => this.handleChange(e.target.value)}/>
          <br></br>
          <br></br>
          <button onClick={this.add}>Add</button>
          <button onClick={this.cle}>Clear</button>
          <br></br>
          <br></br>
          <hr></hr>
          <div className="try" dangerouslySetInnerHTML={this.add()}></div>
        </div>
      </div>
    );
  }

}

export default Item;
