import React, { Component } from "react";

class Item extends React.Component{

  constructor(props:any)
  {
    super(props);
    this.state = { value: "" };
  }

  handleChange(targetV: string) {
    //state = { value: targetV };
    this.setState(targetV);
  }

  add (){
    let x = document.getElementsByClassName("try").item(0)!;
    x.innerHTML = x.innerHTML + "<li>" + this.state + "</li><br/><hr/>";
    console.log(x.innerHTML);
  };

  cle()
  {
    let x = document.getElementsByClassName("try").item(0)!;
    x.innerHTML = "";
  }

  render(): React.ReactNode {
    return (
      <div className="card">
        <h2>{this.props.text}</h2>
        <div className="actions">
          <input type="text" placeholder="Enter List Items" onChange={(e) => handleChange(e.target.value)}/>
          <br></br>
          <br></br>
          <button onClick={add}>Add</button>
          <button onClick={cle}>Clear</button>
          <br></br>
          <br></br>
          <hr></hr>
          <div className="try"></div>
        </div>
      </div>
    );
  }

}

export default Item;
