    function handleChange(props:any)
    {
        state = { value: props};
        console.log(state)
    }

    return(
        <div className="card">
            <h1>{props.text}</h1>
            <div className="actions">
                <textarea className="text" value = {state.value}
                onChange={this.handleChange('value')}></textarea>
            </div>
            {/* {modalIsOpen && <Modal onCancel={ModalHandle} onConfirm={ModalHandle}/>}
            {modalIsOpen && <BackDrop onClick={ModalHandle} />} */}
        </div>
    );
}

