import React, { ChangeEventHandler } from 'react';
import './App.css';

class headView extends React.Component{
  static handleChange: ChangeEventHandler<HTMLTextAreaElement> | undefined;
  constructor(props)
  {
    super(props);
    this.handleChange = this.handleChange.bind(this)
    this.state = {value:  ''}
  }

  handleChange(e)
  {
    this.setState({value : e.target.value});
  }

  getHead()
  {
    return this.state.value;
  }
}

function App() {
  return (
    <div className = "content">
      <h3>Header</h3>
      <textarea id = "head"
      onChange={headView.handleChange}/><br/>
      <h3>Body</h3>
      <textarea id = "body"/><br/>
      <h3>Signature</h3>
      <textarea id = "sign"/>
      <br/><br/>
      <hr></hr>
      <h2>Preview</h2>
      <hr></hr>
      <div id='headpre'></div>
    </div>
  );
}

export default App;
